function goo() {
	fetch('https://v1.hitokoto.cn')
		.then(function(res) {
			return res.json();
		})
		.then(function(data) {
			var hitokoto = document.getElementById('hitokoto');
			hitokoto.innerHTML = data.hitokoto;
			$("#hitokoto").fadeIn(1000);
		})
		.catch(function(err) {
			console.error(err);
		})
	setTimeout(function() {
		$("#hitokoto").fadeOut(1000);
	}, 5000);
}
setInterval("goo()", 5800);
