//分页
pagesize = 1;
document.getElementById("PageUp").onclick = function() {
	pagesize = pagesize - 1;
	if (pagesize == 0) {
		pagesize = 1;
		layui.layer.msg("没有更多了")
	} else {
		none = pagesize + 1
		document.getElementById("Page" + pagesize).style.display = "block";
		document.getElementById("Page" + none).style.display = "none";
		document.getElementById("PageSize").innerHTML = "第" + pagesize + "页";
	}
}
document.getElementById("PageDown").onclick = function() {
	pagesize = pagesize + 1;
	if (pagesize == 2) {
		pagesize = 1;
		layui.layer.msg("没有更多了")
	} else {
		none = pagesize - 1;
		document.getElementById("Page1").style.display = "none";
		document.getElementById("Page" + pagesize).style.display = "block";
		document.getElementById("Page" + none).style.display = "none";
		document.getElementById("PageSize").innerHTML = "第" + pagesize + "页";
	}
}
